FB.isEmail = function(psEmail) 
{
    if(FB.debug)
        console.log(FB.getCounter() + 'FB.isEmail');

    return typeof psEmail==='string' && /^[\w+\d+._]+\@[\w+\d+_+]+\.[\w+\d+._]{2,8}$/.test(psEmail);
}

