FB.getUserSelected = function()
{
    if(FB.debug)
        console.log(FB.getCounter() + 'FB.getUserSelected');

    var sUserSelected = $('footer > div.outputText', window.parent.document).text()
                        .replace(/\r?\n|\r/g, '')
                        .replace(/&ZeroWidthSpace/g, '')
                        .replace(/  +/g, ' ');

    return sUserSelected;
}

